import { useState } from 'react';
import { Button, Form, Jumbotron, Modal } from 'react-bootstrap';
import { navigate, A}  from 'hookrouter';
import Tarefa from '../models/tarefa.model';
import axios from 'axios';

function CadastrarTarefa(){

    const API_URL_CADASTRAR_TAREFAS = 'http://localhost:3001/gerenciador-tarefas'

    const [tarefa, setTarefa] = useState('');
    const [formValidado, setFormValidado] = useState(false);
    const [exibirModal, setExibirModal] = useState(false);
    const [exibirModalErro, setExibirModalErro] = useState(false);

    async function cadastrar(event){
      event.preventDefault();
      setFormValidado(true);
      if (event.currentTarget.checkValidity() === true) {
          try {
            const novaTarefa = new Tarefa(null, tarefa, false);
            await axios.post(API_URL_CADASTRAR_TAREFAS, novaTarefa);
            setExibirModal(true);
          } catch(err) {
            setExibirModalErro(true);
          }

      }
    }

    function handleTxtTarefa(event){
     setTarefa(event.target.value);
   }


   function handleFecharModal(){
       navigate('/');
   }

   function handleFecharModalErro(){
        setExibirModalErro(false);
   }

    return (
        <div>
            <h3 className="text-center" >Cadastrar</h3>
            <Jumbotron>
                <Form 
                  validated={formValidado}
                  noValidate
                  onSubmit={cadastrar}
                >
                    <Form.Group>
                        <Form.Label>Tarefa</Form.Label>
                        <Form.Control type="text" 
                        placeholder="digite a tarefa"
                        minLength="5"
                        maxLength="100"
                        required
                        value={tarefa}
                        onChange={handleTxtTarefa}
                        data-testid="txt-tarefa"
                        /> 
                        <Form.Control.Feedback type="invalid">
                            A Tarefa de contem ao menos 5 caracteres
                        </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group className="text-center">
                        <Button
                        variant="success"
                        type="submit"
                        data-testid="btn-cadastrar">Cadastrar
                        </Button>
                        &nbsp;
                        <A href="/" className="btn btn-light">Voltar</A>
                    </Form.Group>
                </Form>  
                <Modal show={exibirModal} onHide={handleFecharModal}  data-testid="modal" >
                    <Modal.Header closeButton>
                        <Modal.Title>Sucesso</Modal.Title>
                    </Modal.Header>
                    <Modal.Body  data-testid="modal-body" >
                        Tarefa Adicionada com sucesso!
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                        variant="success"
                        onClick={handleFecharModal}
                        >
                           Continuar
                        </Button>
                    </Modal.Footer>
                </Modal>   

                <Modal show={exibirModalErro} onHide={handleFecharModalErro}  data-testid="modal" >
                    <Modal.Header closeButton>
                        <Modal.Title>Erro</Modal.Title>
                    </Modal.Header>
                    <Modal.Body  data-testid="modal-body" >
                        Erro ao tentar adicionar tarefa. Tente novamente.
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                        variant="warning"
                        onClick={handleFecharModalErro}
                        >
                           Fechar
                        </Button>
                    </Modal.Footer>
                </Modal>   
            </Jumbotron>
        </div>
    )
}

export default CadastrarTarefa;
